css:
.designportal h1,
.designportal h2, .designportal h3, .designportal h4,
.designportal p,
.designportal td,
.designportal th,
.designportal ul, .designportal dl, .designportal dd, .designportal dt, .designportal ol, .designportal button {
  margin: 0;
  padding: 0;
}

.designportal ul {
  list-style: disc;
}

.designportal ol {
  list-style: decimal;
}

.designportal button {
  border: none;
}

.designportal-advanced {
  display: block;
  position: relative;
  padding: 20px 0 5px;
  background: #d8d8d8;
}

.designportal-advanced .advanced-list-item {
  font-size: 1rem;
  margin-bottom: 5px;
}

@media screen and (max-width: 1000px) {
  .designportal-advanced .advanced-list-item {
    font-size: 1.6rem;
    margin-bottom: 10px;
  }
}

.designportal-advanced .advanced-panel--active {
  background-color: #ffffff;
  color: #333333;
  z-index: 2;
}

.designportal-advanced .advanced-container {
  margin: 0 auto;
  max-width: 1800px;
  width: 100%;
}

.designportal-advanced .advanced-tab-container {
  display: flex;
  list-style-type: none;
  text-align: center;
  margin: 0 10px 0 10px;
  color: #333333;
}

@media screen and (max-width: 576px) {
  .designportal-advanced .advanced-tab-container {
    margin: 0 10px;
  }
}

.designportal-advanced .advanced-tab {
  display: block;
  flex: 1;
  width: 100%;
  margin-right: 20px;
  align-content: stretch;
  padding: 15px 10px;
  position: relative;
  font-size: 1.2rem;
  border-top-left-radius: 5px;
  border-top-right-radius: 5px;
  background-color: #333333;
  color: #ffffff;
  cursor: pointer;
}

.designportal-advanced .advanced-tab:last-child {
  margin-right: 0;
}

.designportal-advanced .advanced-tab:hover {
  background-color: #666666;
  color: #ffffff;
}

.designportal-advanced .advanced-tab:focus {
  background-color: #ffffff;
  color: #333333;
}

@media screen and (max-width: 1000px) {
  .designportal-advanced .advanced-tab {
    font-size: 1.2rem;
  }
}

@media screen and (max-width: 576px) {
  .designportal-advanced .advanced-tab {
    margin-right: 10px;
  }
}

.designportal-advanced .advanced-tab--active {
  background-color: #ffffff;
  color: #333333;
}

.designportal-advanced .advanced-panel-container {
  display: block;
  position: relative;
  margin: 0 10px 10px;
  background-color: #ffffff;
  border-bottom-left-radius: 5px;
  border-bottom-right-radius: 5px;
}

.designportal-advanced .advanced-list-container {
  display: flex;
}

@media screen and (max-width: 576px) {
  .designportal-advanced .advanced-list-container {
    display: block;
  }
}

.designportal-advanced .advanced-list {
  flex: 1;
  padding: 0 0 18px 30px;
  margin: 0 15px;
}

.designportal-advanced .advanced-list:first-child {
  margin-left: 0;
}

.designportal-advanced .advanced-list:last-child {
  margin-right: 0;
}

@media screen and (max-width: 576px) {
  .designportal-advanced .advanced-list {
    margin: 0;
  }
}

.designportal-advanced .advanced-panel-contents {
  display: none;
  padding: 10px 0 0 0;
  clear: both;
  overflow: hidden;
  color: #333333;
}

@media screen and (max-width: 1000px) {
  .designportal-advanced .advanced-panel-contents {
    padding: 10px 0 0 0;
  }
}

.designportal-advanced .advanced-panel-contents--active {
  overflow: hidden;
  display: block;
}

.designportal-advanced .advanced-panel-contents-group {
  display: flex;
}

@media screen and (max-width: 1000px) {
  .designportal-advanced .advanced-panel-contents-group {
    flex-direction: column;
  }
}

.designportal-advanced .advanced-data {
  display: block;
}

.designportal-advanced .advanced-img {
  width: 100%;
}

.designportal-advanced .advanced-data--graph {
  width: 100%;
}

.designportal-advanced .advanced-heading1 {
  font-size: 2rem;
  font-weight: bold;
  margin-bottom: 20px;
}

@media screen and (max-width: 1000px) {
  .designportal-advanced .advanced-heading1 {
    font-size: 2.6rem;
  }
}

.designportal-advanced .advanced-heading2 {
  font-size: 1.5rem;
  font-weight: bold;
  margin-bottom: 20px;
}

@media screen and (max-width: 1000px) {
  .designportal-advanced .advanced-heading2 {
    font-size: 2rem;
  }
}

.designportal-advanced .advanced-text {
  font-size: 1rem;
  margin-bottom: 20px;
}

@media screen and (max-width: 1000px) {
  .designportal-advanced .advanced-text {
    font-size: 1.2rem;
  }
}

.designportal-advanced .advanced-panel-contents-unit {
  flex: 1;
  margin: 0 30px 30px 0;
}

.designportal-advanced .advanced-panel-contents-unit:last-child {
  margin-right: 0;
}

/* Standard portal weget */
.gaia-argoui-widget {
  border: 1px solid #eee;
}

/* Map image */
.mt-contents {
  background: url('https://raw.githubusercontent.com/maptomo/mt-kinportal/master/img/mt3.jpg');
  background-size: cover;
}

/* Grid css */
.gird-container {
  margin: 0 auto;
  padding: 0px;
  width: 100%;
  columns: auto 13em;
  column-gap: 0;
}

.grid-item {
  margin: 0 5px 5px 10px;
  width: 85%;
  height: 100%;
  padding: 8px;
  background-color: #fff;
  border: 1px solid #eee;
  opacity: 0.8;
}

.grid-item img {
  width: 100%;
}

/*iframe css*/
.map-container {
  margin: 0 10px 5px 10px;
}

.map-iframe {
  width: 100%;
  height: 500px;
  -webkit-filter: hue-rotate(100deg);
  filter: hue-rotate(100deg);
	display: block;
	pointer-events: auto; /* or none auto */
	position: relative; /* IE needs a position other than static */
}

js:
/*-------------------------------------------------------------------
Maptomo Sample kintone portal csutomize.
@maptomo 2019
https://github.com/ushiront/maptomo-kinportal
-------------------------------------------------------------------*/

(function() {

  "use strict";

  /*
  Tab action is template.
  https://github.com/kintone
  portal-design-templates
  */

  var buttonEls = document.querySelectorAll('.advanced-tab');
  var tabPanelEls = document.querySelectorAll('.advanced-panel-contents ');

  var removeAllButtonActive = function() {
    buttonEls.forEach(function(buttonEl) {
      buttonEl.classList.remove('advanced-tab--active');
    });
  };

  var removeAllButtonExpanded = function() {
    buttonEls.forEach(function(buttonEl) {
     buttonEl.setAttribute('aria-expanded', 'false');
    });
  };

  var removeAllTabPanelActive = function() {
    tabPanelEls.forEach(function(tabPanelEl) {
      tabPanelEl.classList.remove('advanced-panel-contents--active');
    });
  };

  var getTabNumber = function(buttonEl) {
    var number = 0;
    for (; number < buttonEls.length; number++) {
      if (buttonEls[number] === buttonEl) {
        break;
      }
    }
    return number;
  };


  var handleClick = function(evt) {
    removeAllButtonActive();
    evt.target.classList.add('advanced-tab--active');

    removeAllButtonExpanded();
    evt.target.setAttribute('aria-expanded', 'true');

    var tabNumber = getTabNumber(evt.target);

    removeAllTabPanelActive();
    tabPanelEls[tabNumber].classList.add('advanced-panel-contents--active');
  };

  buttonEls.forEach(function(buttonEl) {
    buttonEl.addEventListener('click', handleClick);
  });

  /*
  Original code.
  */

  /*
  Load MagicGrid min.js
  https://github.com/e-oj/Magic-Grid
  */
  var script_magic_grid = document.createElement('script');
  script_magic_grid.src = 'https://unpkg.com/magic-grid/dist/magic-grid.min.js';
  document.body.appendChild(script_magic_grid);

  /*
  Draw standard tab to kintone css
  ** It may stop working if the CSS specification changes.
  */
  console.log(location.href);
  var drawStandardVeiw = function(viewType) {
    var st_left = document.getElementsByClassName('st-left');
    var st_right = document.getElementsByClassName('st-right');

    switch(viewType){
      case 'portal.show':
        st_left[0].appendChild(document.getElementsByClassName('ocean-portal-body-left')[0]);
        st_right[0].appendChild(document.getElementsByClassName('ocean-portal-body-right')[0]);
        break;

      case 'mobile.portal.show': 
        st_left[0].appendChild(document.getElementsByClassName('gaia-mobile-v2-portal-announcement')[0]);
        st_left[0].appendChild(document.getElementsByClassName('gaia-mobile-v2-portal-appwidget')[0]);
        st_left[0].appendChild(document.getElementsByClassName('gaia-mobile-v2-portal-spacewidget')[0]);
        break;

      default:
        break;
    }
  }

  /*
  Draw MagicGrid by event type.
  */
  var drawGrid = function(type) {

    var maxColumns = 6;
    (type === 'mobile.portal.show')? maxColumns = 3 : maxColumns = 6;
    if (type === 'mobile.portal.show'){
      var container = document.getElementsByClassName('gird-container');
      container.setAttribute('column', '3 11em');
    }

    var magicGrid = new MagicGrid({
      container: '#gird-container',
      static: false,
      items: 100,
      animate: true,
      gutter: 30,
      maxColumns: maxColumns,
      useMin: true
    });
    magicGrid.listen();
  };

  /*
  Get viewer type. pc type or mobile type.
  */
  var getViewType = function() {

    var vw_type = 'portal.show';
    var pathname = window.location.pathname;
    if (pathname.indexOf('/k/m') !== -1) {
      vw_type = 'mobile.portal.show'; 
    }
    console.log(vw_type);
    return vw_type;
  }

  // Get view type return portal.show|mobile.portal.show
  var viewType = getViewType();

  // Tab2 --------------------------------------------
  var query = {
    'app': 20, //appID
    'query': 'order by Update_day desc',
    'size': 100 //max 500
  };

  var elem_main = document.getElementById('gird-container');
  var local_domain = location.hostname;

  // Post kintone cursor api
  kintone.api(kintone.api.url('/k/v1/records/cursor', true), 'POST', query, function(resp1) {
    // Get records by cursor id
    return kintone.api(kintone.api.url('/k/v1/records/cursor', true), 'GET', {'id':resp1.id}, function(resp) {

      var records = resp.records;
      var i = 0;
      for (; i < records.length; i++) {

        // -- Link element
        var elem_link = document.createElement('a');
        var link = records[i]['Link']['value'];

        // Check the value is numeric
        if (isFinite(link)){
          link =  location.origin + "/k/" + records[i]['Link']['value'];
        }

        if (link.indexOf(local_domain) === -1){ 
          elem_link.setAttribute('target', '_blank'); 
        }
        elem_link.setAttribute('href', link);

        // -- Image element
        var elem_img = document.createElement('img');
        elem_img.setAttribute('src', records[i]['Image']['value']);

        // -- Item element
        var elem_item = document.createElement('div');
        elem_item.setAttribute('class', 'grid-item');

        // -- Apend
        elem_link.appendChild(elem_img);
        elem_link.appendChild(document.createTextNode(records[i]['Name']['value']));
        elem_item.appendChild(elem_link);
        elem_main.appendChild(elem_item);

      }
      drawGrid(viewType);

    }, function(error) {
      // error
      console.log(error);
      elem_main.appendChild(document.createTextNode(error.message))
    });  
  });

  // Tab1 --------------------------------------------
  drawStandardVeiw(viewType);

})();
